<template>
  <section>
    <table v-if="address && state.amount" class="assets-table">
      <thead class="assets-table__thead">
        <tr>
          <td>Locked Asset</td>
          <td></td>
          <td class="assets-table__align-right">Locked balance</td>
        </tr>
      </thead>
      <tbody>
        <tr class="assets-table__row">
          <td class="assets-table__denom">
            <div class="sp-denom-marker">
              <div class="token-avatar">
			    {{ "mand".slice(0, 1) }}
			  </div>
            </div>
            <div class="sp-denom-name">
              <span>
			    {{ "mand".toUpperCase() }}
			  </span>
            </div>
          </td>
          <td></td>
          <td class="assets-table__amount">
            {{ new Intl.NumberFormat('en-GB').format(state.amount) }}
          </td>
        </tr>
      </tbody>
    </table>
  </section>
</template>

<script lang="ts">
import { computed, defineComponent, nextTick, watch, reactive } from 'vue'
import { useStore } from 'vuex'

import { useAddress, useAssets } from '@starport/vue/src/composables'
import axios from 'axios'

export interface State {
  amount: int
}

export let initialState: State = {
  amount: 0
}

export default defineComponent({
  name: 'LockedAssets',

  setup(props) {
    // store
    let $s = useStore()

    let state = reactive(initialState)

    // composables
    let { address } = useAddress({ $s })

    let lockedBalance = async () => {
      console.log('called')
      if (address.value) {
      	try {
	        let API_COSMOS = computed<string>(() => $s.getters['common/env/apiCosmos'])

	        let aggregateVotesCasted = await axios.get(
	          `${API_COSMOS.value}` +
	            `/mande-chain/voting/aggregate_votes_casted/`+
	            `${address.value}`
	          )

	        state.amount = aggregateVotesCasted.data.aggregateVotesCasted.positive / 10**6 + aggregateVotesCasted.data.aggregateVotesCasted.negative / 10**6
      	} catch {
      		state.amount = 0	
      	}
      } else {
      	state.amount = 0
      }
    }

    watch(
      () => address.value,
      async () => {
        await lockedBalance()
      }
    )


    return {
      address,
      state
    }
  }
})
</script>

